// Calculate weekly financial summary
const transactions = $input.all();
let totalCredit = 0;
let totalDebit = 0;
let creditCount = 0;
let debitCount = 0;
const categories = {};

transactions.forEach(transaction => {
  const data = transaction.json || transaction;

  // Parse amount - remove any currency symbols and convert to number
  const amountStr = data.Amount || data.amount || "0";
  const amount = parseFloat(amountStr.toString().replace(/[^\d.-]/g, '')) || 0;

  // Get transaction type
  const type = (data["Transaction Type"] || data.transactionType || "").toLowerCase();

  // Get category
  const category = data.Category || data.category || 'Unknown';

  // Process based on transaction type
  if (type === 'credit' || type.includes('credit')) {
    totalCredit += amount;
    creditCount++;
  } else if (type === 'debit' || type.includes('debit')) {
    totalDebit += amount;
    debitCount++;
  }

  // Category breakdown
  if (!categories[category]) {
    categories[category] = { amount: 0, count: 0 };
  }
  categories[category].amount += amount;
  categories[category].count++;
});

const netFlow = totalCredit - totalDebit;
const weekStart = new Date();
weekStart.setDate(weekStart.getDate() - 7);

// Return in the exact format requested
return {
  weekStart: weekStart.toISOString().split('T')[0],
  weekEnd: new Date().toISOString().split('T')[0],
  totalCredit,
  totalDebit,
  netFlow,
  creditCount,
  debitCount,
  totalTransactions: creditCount + debitCount,
  categories,
  averageTransaction: (totalCredit + totalDebit) / (creditCount + debitCount) || 0
};

